import React, { useState, useEffect } from "react";
import { 
    View,
    Text,
    KeyboardAvoidingView,
    Platform 
} from "react-native";

import FormInput from '../../../../components/FormInput';
import FormButton from '../../../../components/FormButton';
import SocialButton from '../../../../components/SocialButton';

import firebase from "../../../config/firebaseconfig";
import styles from "./style";
import { MaterialCommunityIcons } from "@expo/vector-icons";

export default function LoginTutor({ navigation }){
    const [email, setEmail] = useState()
    const [senha, setSenha] = useState()
    const [errorLogin, setErrorLogin] = useState("")

    const loginFirebase = ()=>{
        firebase.auth().signInWithEmailAndPassword(email, senha)
        .then((userCredential) => {
            // Signed in
            let usuario = userCredential.user;
            navigation.navigate("Lista de Jogadores", { idUsuario: usuario.uid})
            // ...
        })
        .catch((error) => {
            setErrorLogin(true)
            let errorCode = error.code;
            let errorMessage = error.message;
        });
    }

    useEffect(()=>{
        firebase.auth().onAuthStateChanged((usuario) => {
            if (usuario) {
              // User is signed in, see docs for a list of available properties
              // https://firebase.google.com/docs/reference/js/firebase.User
              navigation.navigate("Lista de Jogadores", {idUsuario: usuario.uid})
            }
        });
    }, [])

    return(
        <KeyboardAvoidingView
            behavior={Platform.OS === "ios" ? "padding" : "height"}
            style={styles.container}
        >
            <Text style={styles.title1}>Olá Tutor!</Text>
            <Text style={styles.title2}>Faça seu login abaixo!</Text>
            
            <FormInput
                labelValue={email}
                onChangeText={(text) => setEmail(text)}
                placeholderText="Digite seu e-mail"
                iconType="user"
                keyboardType="email-address"
                autoCapitalize="none"
                autoCorrect={false}
            />

            <FormInput
              labelValue={senha}
              onChangeText={(text) => setSenha(text)}
              placeholderText="Digite sua senha"
              iconType="lock"
              secureTextEntry={true}
            />

            {errorLogin === true
            ?
            <View style={styles.contentAlert}>
                <MaterialCommunityIcons
                    name="alert-circle"
                    size={24}
                    color="#bdbdbd"
                />
                <Text style={styles.warningAlert}>E-mail ou Senha inválidos!</Text>
            </View>
            :
            <View />
            }
            
            { email==="" || senha === ""
            ?
            <FormButton
                disabled={disabled}
                buttonTitle="Entrar"
            />
            :   
            <FormButton
                buttonTitle="Entrar"
                onPress={loginFirebase}
            />            
            }

            <Text style={styles.registration}>
                Ainda não tenho cadastro! 
                &nbsp;
            <Text
            style={styles.linkSubscribe}
            onPress={()=> navigation.navigate("Cadastrar Tutor")}
            >
                Quero me Cadastrar!
            </Text>
            </Text>
            
            <View style={{height:30}}/>
            
            {Platform.OS === 'android' ? (
                <View>
                <SocialButton
                    buttonTitle="Sign Up with Facebook"
                    btnType="facebook"
                    color="#4867aa"
                    backgroundColor="#e6eaf4"
                    onPress={() => {}}
                />

                <SocialButton
                    buttonTitle="Sign Up with Google"
                    btnType="google"
                    color="#de4d41"
                    backgroundColor="#f5e7ea"
                    onPress={() => {}}
                />
                </View>
            ) : null}   
        </KeyboardAvoidingView>
    );
}
